import React, { useState } from "react";
import { startOfDay, addDays, addMinutes, format } from "date-fns";
import "../Css/doctors.css";

const Buttonforbooking = () => {
  const [selectedDate, setSelectedDate] = useState(null);
  const [bookedSlots, setBookedSlots] = useState([]);
  const [selectedSlot, setSelectedSlot] = useState(null);

  const generateTimeSlots = (date) => {
    const timeSlots = [];
    const interval = 30; // minutes
    const numberOfSlots = 8;

    for (let i = 0; i < numberOfSlots; i++) {
      const startTime = addMinutes(date, i * interval);
      const endTime = addMinutes(startTime, interval);
      timeSlots.push({ start: startTime, end: endTime });
    }

    return timeSlots;
  };

  const handleSlotClick = (date) => {
    setSelectedDate(date);
    setSelectedSlot(null); // Reset selected slot when date changes
  };

  const handleTimeSlotSelect = (slot) => {
    setSelectedSlot(slot);
  };

  const isSlotBooked = (slot) => {
    return bookedSlots.some((booked) => slot.start === booked.start);
  };

  const availableDates = [...Array(7)].map((_, index) =>
    addDays(startOfDay(new Date()), index)
  );

  return (
    <div className="booking">
      {availableDates.map((date) => (
        <div key={date.getTime()} className="date-container">
          <button
            className={`date-button ${
              selectedDate === date ? "selected" : ""
            }`}
            onClick={() => handleSlotClick(date)}
          >
            {format(date, "MMM d, yyyy")}
          </button>
          {selectedDate && selectedDate.getTime() === date.getTime() && (
            <div className="time-slots">
              {generateTimeSlots(date).map((slot) => (
                <button
                  key={slot.start.getTime()}
                  className={`time-slot ${
                    isSlotBooked(slot) ? "booked" : "available"
                  } ${selectedSlot === slot ? "selected" : ""}`}
                  onClick={() => handleTimeSlotSelect(slot)}
                >
                  {format(slot.start, "hh:mm a")}
                </button>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default Buttonforbooking;
