import React, { useState, useEffect } from "react";
import data from "./db.json";
import "../Css/doctors.css";
import Buttonforbooking from "./bookingbutton";
import { startOfDay, addDays, format } from "date-fns";
import MapboxMap from "./map"

const Doctors = () => {
  const doctorsPerPage = 4; // Number of doctors per page
  const [content, change_data] = useState(data);
  const [currentPage, setCurrentPage] = useState(1);

  const today = startOfDay(new Date());
  const availableDates = [...Array(14)].map((_, index) =>
    format(addDays(today, index), "MMM d ")
  );

  useEffect(() => {
    // You can perform any additional logic here when content or currentPage changes.
    // For now, we won't add any additional logic.
  }, [content, currentPage]);

  // Calculate the index range for the current page
  const startIndex = (currentPage - 1) * doctorsPerPage;
  const endIndex = startIndex + doctorsPerPage;

  // Slice the data array to get doctors for the current page
  const doctorsForPage = content.slice(startIndex, endIndex);


  return (
    <div className="Doctors">



      <div className="">
        <div className="Doctor-heading">
      <h2>
          Doctors Available from {availableDates[0]} to {availableDates[availableDates.length-1]}
        </h2>
        <p>{doctorsForPage.length} Doctors Available</p></div>
      {doctorsForPage.map(({ id, image, first_name, role, address }) => (

        <div className="parent-element">
        <div className="Doctors-card-container" key={id}>
        <div class="sc-eVspGN kEGmuv">
          
            <div data-test="polaris-doctor-photo" class="sc-bhNKFk dgcimL">
                <img alt="Dr. John Abraham" width="96" height="96"
                     src="//d3wnzga3fpd9a.cloudfront.net/photos/Dr-John-Abraham-MD-416658-circle_medium__v2__.png"
                     loading="eager" itemprop="image" data-test="default-doctor-photo-image"/>
            </div>
        </div>
          <div id="Doctor-details">
            <h1>{first_name}</h1>
            <p>{role}</p>
            <p>{address}</p>
          </div>
          
        </div>
        <div>{<Buttonforbooking/>}</div>
        </div>
      ))}
   
      <div className="pagination">
        {Array.from({ length: Math.ceil(content.length / doctorsPerPage) }, (_, index) => (
          <button id="page-button"
            key={index + 1}
            onClick={() => setCurrentPage(index + 1)}
            className={currentPage === index + 1 ? "active" : ""}
          >
            {index + 1}
          </button>
        ))}
      </div>
      </div>
      
      <div>{<MapboxMap/>}</div>
    </div>
  );
};

export default Doctors;
