import React, { useState } from "react";
import { startOfDay, addDays, format } from "date-fns";
import Modal from "react-modal";
import "../Css/doctors.css";

const Buttonforbooking = () => {
  const today = startOfDay(new Date());
  const availableDates = [...Array(14)].map((_, index) =>
    format(addDays(today, index), "MMM d ")
  );

  function generateTimeSlots() {
    const timeSlots = [];
    const startDate = new Date();
    startDate.setHours(9, 0, 0, 0); // Starting from 9:00 AM

    for (let i = 0; i < 10; i++) {
      const slotDate = new Date(startDate.getTime() + i * 30 * 60 * 1000);
      const formattedSlot = format(slotDate, "h:mm a");
      timeSlots.push(formattedSlot);
    }

    return timeSlots;
  }

  const [selectedDate, setSelectedDate] = useState(null);
  const [modalIsOpen, setModalIsOpen] = useState(false);

  const handleDateButtonClick = (date) => {
    setSelectedDate(date);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setSelectedDate(null);
    setModalIsOpen(false);
  };

  return (
    <div className="booking">
      <div className="date-list">
        {availableDates.map((date) => (
          <button
            key={date}
            className="date-button"
            onClick={() => handleDateButtonClick(date)}
          >
            {date}
            <br />
            10 appts
          </button>
        ))}
      </div>
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        contentLabel="Time Slots Modal"
        overlayClassName="custom-overlay" 
      >
        <h2>Select a Time Slot for {selectedDate}</h2>
        <ul>
          {generateTimeSlots().map((timeSlot) => (
            <li key={timeSlot}>{timeSlot}</li>
          ))}
        </ul>
        <button onClick={closeModal}>Close</button>
      </Modal>
    </div>
  );
};

export default Buttonforbooking;
